//import logo from './logo.svg';
import { useEffect, useState } from 'react';
import './App.css';
import { Button, Card, CardGroup } from 'react-bootstrap';
import { Col } from 'react-bootstrap';
import {Row} from 'react-bootstrap';

function App() {
    const [avatarURL, setAvatarURL] = useState();
    const [githubUsername, setGitHubUsername] = useState();
    const [repoData, setRepoData] = useState();

  async function repoDataURL() {
    //Get repo data about github user
    await fetch("https://api.github.com/users/Rigiih7/repos")
      .then((res) => res.json())
      .then(
        (result) => {
          console.log(36, result);
          const list = result.map((item) => (
            <div className="text-center">
              <a target="_blank" href={item.svn_url}>
                {item.name}
              </a>
            </div>
          ));
          setRepoData(list);
        },
        (error) => {
          console.log(error);
        }
      );
  }

    useEffect(() => {
      fetch("https://api.github.com/users/Rigiih7")
      .then((res) => res.json())
      .then(
        (result) => {
          console.log(result);
          setAvatarURL(result.avatar_url);
          setGitHubUsername(result.login);
        },
        (error) => {
          console.log(error);
        }
      );

    }, []);
    return (
      <Row xs={1} md={2} className="g-4">
      {Array.from({ length: 2 }).map((_, idx) => (
        <Col key={idx}>


    <Card style={{ width: '18rem' }} className='mb-4'>
      <Card.Img variant="top" src={avatarURL} />
      <Card.Body>
        <Card.Title>
          {githubUsername}
        </Card.Title>

        <Button variant="primary" onClick = {repoDataURL}>List my repositories!</Button>
      </Card.Body>
    </Card>
    {repoData}
    </Col>
    ))}
    </Row>
  );
}

export default App;
