import { useEffect, useState } from 'react';
import './App.css';
import { Button, Card, CardGroup } from 'react-bootstrap';

function App() {
  const [avatarURL, setAvatarURL] = useState();
  const [avatar1URL, setAvatar1URL] = useState();
  const [githubUsername, setGitHubUsername] = useState();
  const [githubUsername1, setGitHubUsername1] = useState();
  const [repoData, setRepoData] = useState([]);
  const [repoData1, setRepoData1] = useState([]);

  async function repoDataURL(username, setAvatar, setGitHub, setRepoData) {
    await fetch(`https://api.github.com/users/${username}`)
      .then((res) => res.json())
      .then(
        (result) => {
          console.log(result);
          setAvatar(result.avatar_url);
          setGitHub(result.login);
          const list = result.repos.map((item) => (
            <div key={item.id} className="text-center">
              <a target="_blank" href={item.svn_url}>
                {item.name}
              </a>
            </div>
          ));
          setRepoData(list);
        },
        (error) => {
          console.log(error);
        }
      );
  }

  useEffect(() => {
    // Fetch data for the first user
    repoDataURL('Rigiih7', setAvatarURL, setGitHubUsername, setRepoData);

    // Fetch data for the second user
    repoDataURL('TamaraLumumba', setAvatar1URL, setGitHubUsername1, setRepoData1);
  }, []);

  return (
    <div className='App padding:100px w-100 min-vh-100 justify-content-center align-items-center d-flex flex-column'>
      <CardGroup>
        {/* Card for the first user */}
        <Card style={{ width: '18rem' }} className='mb-3 p-4'>
          <Card.Img variant="top" src={avatarURL} />
          <Card.Body>
            <Card.Title>{githubUsername}</Card.Title>
            <Button variant="primary" onClick={() => repoDataURL('Rigiih7', setAvatarURL, setGitHubUsername, setRepoData)}>
              List my repositories!
            </Button>
          </Card.Body>
          {repoData}
        </Card>

        {/* Card for the second user */}
        <Card style={{ width: '18rem' }} className='mb-3 p-4'>
          <Card.Img variant="top" src={avatar1URL} />
          <Card.Body>
            <Card.Title>{githubUsername1}</Card.Title>
            <Button variant="primary" onClick={() => repoDataURL('TamaraLumumba', setAvatar1URL, setGitHubUsername1, setRepoData1)}>
              List my repositories!
            </Button>
          </Card.Body>
          {repoData1}
        </Card>
      </CardGroup>
    </div>
  );
}

export default App;
