//import logo from './logo.svg';
import { useEffect, useState } from 'react';
import './App.css';
import { Button, Card, CardGroup } from 'react-bootstrap';
import { Col } from 'react-bootstrap';
import {Row} from 'react-bootstrap'

function App() {
    const [avatarURL, setAvatarURL] = useState();
    const [githubUsername, setGitHubUsername] = useState();
    const [repoData, setRepoData] = useState();

  async function repoDataURL() {
    //Get repo data about github user
    await fetch("https://api.github.com/users/Rigiih7/repos")
      .then((res) => res.json())
      .then(
        (result) => {
          console.log(36, result);
          const list = result.map((item) => (
            <div className="text-center">
              <a target="_blank" href={item.svn_url}>
                {item.name}
              </a>
            </div>
          ));
          setRepoData(list);
        },
        (error) => {
          console.log(error);
        }
      );
  }

    useEffect(() => {
      fetch("https://api.github.com/users/Rigiih7")
      .then((res) => res.json())
      .then(
        (result) => {
          console.log(result);
          setAvatarURL(result.avatar_url);
          setGitHubUsername(result.login);
        },
        (error) => {
          console.log(error);
        }
      );

    }, []);
    return (
    <div className='App w-100 min-vh-100 justify-content-center align-items-center d-flex flex-column'>

    <Card style={{ width: '18rem' }} className='mb-4'>
      <Card.Img variant="top" src={avatarURL} />
      <Card.Body>
        <Card.Title>
          {githubUsername}
        </Card.Title>

        <Button variant="primary" onClick = {repoDataURL}>List my repositories!</Button>
      </Card.Body>
    </Card>
    {repoData}


    <CardGroup>
    <Card style={{ width: '18rem' }} className='mb-4'>
      <Card.Img variant="top" src={avatarURL} />
      <Card.Body>
        <Card.Title>
          {githubUsername}
        </Card.Title>

        <Button variant="primary" onClick = {repoDataURL}>List my repositories!</Button>
      </Card.Body>
    </Card>

      <Card>
        <Card.Img variant="top" src="holder.js/100px160" />
        <Card.Body>
          <Card.Title>Card title</Card.Title>
          <Card.Text>
            This is a wider card with supporting text below as a natural lead-in
            to additional content. This card has even longer content than the
            first to show that equal height action.
          </Card.Text>
        </Card.Body>
        <Card.Footer>
          <small className="text-muted">Last updated 3 mins ago</small>
        </Card.Footer>
      </Card>
    </CardGroup>    



  </div>
  );
}

export default App;
